export default class extends React.Component {

    static async getInitialProps({query}) {
        let idPodcast = query.id

        let [reqPodcast] = await Promise.all([
            fetch(`https://api.audioboom.com/audio_clips/${idPodcast}.mp3`)
        ])

        let dataPodcast = await reqPodcast.json()
        let podcast =  dataPodcast.body.audio_clip

        return { podcast }
    }

    render() {
        const { podcast } = this.props

        return <div>
            <header>Audio</header>
            
            <h1>{podcast.title}</h1>

            <img src={podcast.urls.image} alt=""/>

            <audio src={podcast.urls.high_mp3}></audio>

            <style jsx>{`
                header {
                    color: #fff;
                    background: #8756ca;
                    padding: 15px;
                    text-align: center;
                }
                .channels {
                    display: grid;
                    grid-gap: 15px;
                    padding: 15px;
                    grid-template-columns: repeat(auto-fill, minmax(160px, 1fr));
                }
                a.channel {
                    display: block;
                    margin-bottom: 0.5em;
                    color: #333;
                    text-decoration: none;
                }
                .channel img {
                    border-radius: 3px;
                    box-shadow: 0px 2px 6px rgba(0,0,0,0.15);
                    width: 100%;
                }
                img {
                    max-height: 100px;
                }
                h2 {
                    padding: 5px;
                    font-size: 0.9em;
                    font-weight: 600;
                    margin: 0;
                    text-align: center;
                }
            `}</style>

            <style jsx global>{`
                body {
                margin: 0;
                font-family: system-ui;
                background: white;
                }
            `}</style>
        </div>
    }
}
