export default class extends React.Component {
    render() {
        return <div>

            <img src="/platzi-logo.png" alt="Platzi"/> 

            <h1>Vanzkyrim!</h1>
            <p>Tierras medias del oriente norte</p>


            <style jsx>{`
                h1 {
                    color: red;
                }
                :global(div p) {
                    color: green;
                }
                img {
                    max-width: 50%;
                    display: block;
                    margin: 0 auto;
                }
            `}</style>

            <style jsx global>{`            
                body {
                    background: #3f5665;
                }
            `}
            </style>
        </div>
    }
}
