export default class extends React.Component {
    render() {
        return <div>

            <img src="/platzi-logo.png" alt="Platzi"/> 

            <h1>Vanzkyrim!</h1>
            <p>Tierras medias del oriente norte</p>


            <style jsx>{`
                div { 
                    margin: 0 auto;
                    alignItems: 'center';
                }
                div * {
                    color: white;
                    margin: 0 auto;
                    textAlign: 'center';
                    alignItems: 'center';
                    justifyContent: 'center';
                }
                img {
                    max-width: 30%;
                    display: block;
                    margin: 0 auto;
                }
            `}</style>

            <style jsx global>{`            
                body {
                    background: #3f5665;
                }
            `}
            </style>
        </div>
    }
}
