import Link from 'next/link'

export default class extends React.Component {

    static async getInitialProps({query}) {
        let idPodcast = query.id

        let [reqPodcast] = await Promise.all([
            fetch(`https://api.audioboom.com/audio_clips/${idPodcast}.mp3`)
        ])

        let dataPodcast = await reqPodcast.json()
        let podcast =  dataPodcast.body.audio_clip

        return { podcast }
    }

    render() {
        const { podcast } = this.props

        return <div>
            <header>Audio</header>

            <div className="modal">
                <div className="clip">
                    <nav>
                        <Link href={`/channel?id=${podcast.channel.id}`}>
                            <a className="close">&lt; Volver</a>
                        </Link>
                    </nav>
                </div>
            </div>
            
            <picture>
                <div style={{ backgroundImage: `url(${clip.urls.image || clip.channel.urls.logo_image.original})` }}></div>
            </picture>
            <h1>{podcast.title}</h1>

            <img src={podcast.urls.image} alt=""/>

            <audio controls>
                <source src={podcast.urls.high_mp3} type="audio/mpeg"/>
                Your browser does not support the audio element.
            </audio>

            <style jsx>{`
                nav {
                    background: none;
                }
                nav a {
                    display: inline-block;
                    padding: 15px;
                    color: white;
                    cursor: pointer;
                    text-decoration: none;
                }
                .clip {
                    display: flex;
                    height: 100%;
                    flex-direction: column;
                    background: #8756ca;
                    color: white;
                }
                picture {
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    flex: 1 1;
                    flex-direction: column;
                    width: auto;
                    padding: 10%;
                }
                picture div {
                    width: 100%;
                    height: 100%;
                    background-position: 50% 50%;
                    background-size: contain;
                    background-repeat: no-repeat;
                }
                .player {
                    padding: 30px;
                    background: rgba(0,0,0,0.3);
                    text-align: center;
                }
                h3 {
            
                }
                h6 {
                    margin: 0;
                    margin-top: 1em;
                }
                audio {
                    margin-top: 2em;
                    width: 100%;
                }

                .modal {
                    position: fixed;
                    top: 0;
                    left: 0;
                    right: 0;
                    bottom: 0;
                    z-index: 99999;
                }
            `}</style>

            <style jsx global>{`
                body {
                margin: 0;
                font-family: system-ui;
                background: white;
                }
            `}</style>
        </div>
    }
}
