export default class extends React.Component {
    render() {
        return <div>
            <h1>Hola Mundo!</h1>
            <p>Welcome to Course of Next.js</p>

            <img src="/platzi-logo.png" alt="Platzi"/> 

            <style jsx>{`
                h1 {
                    color: red;
                }
                :global(div p) {
                    color: green;
                }
            `}</style>

            <style jsx global>{`            
                body {
                    background: blue;
                }
            `}
            </style>
        </div>
    }
}
